import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-deminscri',
  templateUrl: './deminscri.component.html',
  styleUrls: ['./deminscri.component.css'],
})
export class DeminscriComponent implements OnInit {
  title: string = 'Multi-Step Form';
  currentStep: number = 0;
  steps: any[] = [];

  constructor(private fb: FormBuilder) {}

  ngOnInit(): void {
    // Define steps with dynamic fields and validation
    this.steps = [
      {
        title: 'Step 1: Personal Information',
        fields: [
          { label: 'Field 1', type: 'text', formControlName: 'field1' },
          { label: 'Field 2', type: 'number', formControlName: 'field2' },
        ],
        formGroup: this.fb.group({
          field1: ['', Validators.required],
          field2: ['', Validators.required],
        }),
      },
      {
        title: 'Step 2: Ownership Details',
        fields: [
          { label: 'Name', type: 'text', formControlName: 'name' },
          { label: 'Document ID', type: 'text', formControlName: 'documentId' },
        ],
        formGroup: this.fb.group({
          name: ['', Validators.required],
          documentId: ['', Validators.required],
        }),
      },
    ];
  }

  // Navigate to the next step
  nextStep(): void {
    if (this.currentStep < this.steps.length - 1) {
      this.currentStep++;
    }
  }

  // Navigate to the previous step
  previousStep(): void {
    if (this.currentStep > 0) {
      this.currentStep--;
    }
  }

  // Submit the form
  submitForm(): void {
    if (this.allFormsValid()) {
      const formData = this.steps.map((step) => step.formGroup.value);
      console.log('Form Submitted:', formData);
      alert('Form submitted successfully!');
    } else {
      alert('Please complete all required fields.');
    }
  }

  // Check if all steps are valid
  allFormsValid(): boolean {
    return this.steps.every((step) => step.formGroup.valid);
  }
}
<div class="container mt-5">
  <h2>{{ title }}</h2>

  <!-- Multi-Step Form -->
  <div class="form-container">
    <div
      class="form-step"
      *ngFor="let step of steps; let i = index"
      [class.active]="currentStep === i"
    >
      <h3>{{ step.title }}</h3>
      <form [formGroup]="step.formGroup">
        <div *ngFor="let field of step.fields" class="mb-3">
          <label [for]="field.formControlName" class="form-label">{{ field.label }}</label>
          <input
            [type]="field.type"
            [id]="field.formControlName"
            [formControlName]="field.formControlName"
            class="form-control"
            [ngClass]="{
              'is-invalid':
                step.formGroup.get(field.formControlName)?.invalid &&
                step.formGroup.get(field.formControlName)?.touched
            }"
          />
          <div
            *ngIf="step.formGroup.get(field.formControlName)?.invalid && step.formGroup.get(field.formControlName)?.touched"
            class="text-danger"
          >
            This field is required.
          </div>
        </div>
      </form>
    </div>

    <!-- Navigation Buttons -->
    <div class="navigation mt-4">
      <button class="btn btn-secondary" (click)="previousStep()" [disabled]="currentStep === 0">
        Previous
      </button>
      <button
        class="btn btn-primary"
        (click)="nextStep()"
        *ngIf="currentStep < steps.length - 1"
        [disabled]="!steps[currentStep].formGroup.valid"
      >
        Next
      </button>
      <button
        class="btn btn-success"
        *ngIf="currentStep === steps.length - 1"
        (click)="submitForm()"
        [disabled]="!steps[currentStep].formGroup.valid"
      >
        Submit
      </button>
    </div>
  </div>
</div>
