import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-input-form',
  templateUrl: './input-form.component.html',
  styleUrls: ['./input-form.component.css']
})
export class InputFormComponent implements OnInit {
  inputForm: FormGroup;

  constructor(private fb: FormBuilder) {
    // Initialize the form group with controls
    this.inputForm = this.fb.group({
      name: ['', Validators.required],
      surname: ['', Validators.required],
      identityDocument: ['', Validators.required],
      countNumber: ['', Validators.required],
      issueDate: ['', Validators.required],
      issuePlace: ['', Validators.required],
      entityName: ['', Validators.required],
      address: ['', Validators.required],
      paymentMethod: ['', Validators.required]
    });
  }

  ngOnInit(): void {}

  onSubmit(): void {
    if (this.inputForm.invalid) {
      this.showValidationErrors();
    } else {
      alert('Form submitted successfully!');
      console.log(this.inputForm.value);
    }
  }

  private showValidationErrors(): void {
    Object.keys(this.inputForm.controls).forEach((key) => {
      const control = this.inputForm.get(key);
      if (control?.invalid) {
        const label = this.getLabelText(key);
        alert(`${label} مطلوب`);
      }
    });
  }

  private getLabelText(controlName: string): string {
    const labels: { [key: string]: string } = {
      name: 'الاسم',
      surname: 'اللقب',
      identityDocument: 'وثيقة الهوية',
      countNumber: 'عددها',
      issueDate: 'تاريخ إصدارها',
      issuePlace: 'مكان إصدارها',
      entityName: 'تسمية الذات الإعتبارية',
      address: 'العنوان',
      paymentMethod: 'كيفية الدفع'
    };
    return labels[controlName] || controlName;
  }
}
