import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-mainpage',
  templateUrl: './mainpage.component.html',
  styleUrls: ['./mainpage.component.css']
})
export class MainPageComponent implements OnInit {
  formGroup!: FormGroup; // Holds form controls and their validation state
  currentDate: string = ''; // Stores the current date to display in the top bar

  @ViewChild('scrollableWrapper', { static: true })
  scrollableWrapper!: ElementRef; // Reference to the scrollable cards container

  constructor(private fb: FormBuilder) {}

  ngOnInit(): void {
    this.initializeForm();
    this.setCurrentDate();
  }

  /**
   * Initialize the form with form controls and their validators
   */
  private initializeForm(): void {
    this.formGroup = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      surname: ['', [Validators.required, Validators.minLength(3)]],
      identityDocument: ['', Validators.required],
      countNumber: ['', [Validators.required, Validators.pattern('^[0-9]+$')]],
      issueDate: ['', Validators.required],
      issuePlace: ['', Validators.required],
      entityName: [''], // Optional field
      address: ['', Validators.required],
      documentName: ['', Validators.required]
    });
  }

  /**
   * Set the current date to display in the top bar
   */
  private setCurrentDate(): void {
    const today = new Date();
    this.currentDate = today.toLocaleDateString('EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    });
  }

  /**
   * Handle form submission
   */
  onSubmit(): void {
    if (this.formGroup.valid) {
      console.log('Form Submitted Successfully:', this.formGroup.value);
      alert('تم الحفظ بنجاح!');
    } else {
      alert('يرجى التحقق من صحة النموذج قبل الإرسال.');
      this.formGroup.markAllAsTouched(); // Highlights invalid fields
    }
  }

  /**
   * Scroll the cards container to the left
   */
  scrollLeft(): void {
    this.scrollableWrapper.nativeElement.scrollBy({
      left: -200, // Adjust scroll distance
      behavior: 'smooth'
    });
  }

  /**
   * Scroll the cards container to the right
   */
  scrollRight(): void {
    this.scrollableWrapper.nativeElement.scrollBy({
      left: 200, // Adjust scroll distance
      behavior: 'smooth'
    });
  }
}
<div class="container">
    <div class="card shadow-sm">
        <div class="card-header bg-primary text-white text-center">
            <h3 class="mb-0">نموذج إدخال</h3>
        </div>
        <div class="card-body">
            <form [formGroup]="formGroup" (ngSubmit)="onSubmit()">
                <!-- Existing form fields (unchanged) -->

                <!-- Payment Method -->
                <div class="row">
                    <div class="col-md-12 mb-3">
                        <label for="paymentMethod" class="form-label">كيفية الدفع</label>
                        <select class="form-select" id="paymentMethod" formControlName="paymentMethod" [ngClass]="{'is-invalid': formGroup.get('paymentMethod')?.invalid && formGroup.get('paymentMethod')?.touched}">
                            <option selected disabled>اختر كيفية الدفع</option>
                            <option value="صك بنكي">صك بنكي</option>
                            <option value="حوالة بريدية">حوالة بريدية</option>
                            <option value="صك بريدي">صك بريدي</option>
                            <option value="دفع إلكتروني">دفع إلكتروني</option>
                            <option value="نقداً">نقداً</option>
                            <option value="مجاناً">مجاناً</option>
                        </select>
                        <div class="invalid-feedback">يرجى اختيار كيفية الدفع.</div>
                    </div>
                </div>

                <!-- Delivery Method -->
                <div class="row">
                    <div class="col-md-12 mb-3">
                        <label for="deliveryMethod" class="form-label">كيفية التسليم</label>
                        <select class="form-select" id="deliveryMethod" formControlName="deliveryMethod" [ngClass]="{'is-invalid': formGroup.get('deliveryMethod')?.invalid && formGroup.get('deliveryMethod')?.touched}">
                            <option selected disabled>اختر كيفية التسليم</option>
                            <option value="باليد">باليد</option>
                            <option value="عبر البريد">عبر البريد</option>
                            <option value="مجاناً">مجاناً</option>
                        </select>
                        <div class="invalid-feedback">يرجى اختيار كيفية التسليم.</div>
                    </div>
                </div>

                <!-- Document Name -->
                <div class="row">
                    <div class="col-md-12 mb-3">
                        <label for="documentName" class="form-label">تسمية الوثيقة</label>
                        <select class="form-select" id="documentName" formControlName="documentName" [ngClass]="{'is-invalid': formGroup.get('documentName')?.invalid && formGroup.get('documentName')?.touched}">
                            <option selected disabled>اختر تسمية الوثيقة</option>
                            <option value="جواز السفر">جواز السفر</option>
                            <option value="بطاقة الهوية">بطاقة الهوية</option>
                            <option value="رخصة القيادة">رخصة القيادة</option>
                        </select>
                        <div class="invalid-feedback">يرجى اختيار تسمية الوثيقة.</div>
                    </div>
                </div>

                <!-- Submit Buttons -->
                <div class="text-center">
                    <button type="submit" class="btn btn-primary">حفظ</button>
                    <button type="reset" class="btn btn-secondary">إعادة تعيين</button>
                </div>
            </form>
        </div>
    </div>
</div>
