import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-mainpage',
  templateUrl: './mainpage.component.html',
  styleUrls: ['./mainpage.component.css']
})
export class MainPageComponent implements OnInit {
  formGroup!: FormGroup; // Holds form controls and their validation state
  currentDate: string = ''; // Stores the current date to display in the top bar

  constructor(private fb: FormBuilder) {}

  ngOnInit(): void {
    this.initializeForm();
    this.setCurrentDate();
  }

  /**
   * Initialize the form with form controls and their validators
   */
  private initializeForm(): void {
    this.formGroup = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      surname: ['', [Validators.required, Validators.minLength(3)]],
      identityDocument: ['', Validators.required],
      countNumber: ['', [Validators.required, Validators.pattern('^[0-9]+$')]],
      issueDate: ['', Validators.required],
      issuePlace: ['', Validators.required],
      entityName: [''], // Optional field
      address: ['', Validators.required],
      documentName: ['', Validators.required]
    });
  }

  /**
   * Set the current date to display in the top bar
   */
  private setCurrentDate(): void {
    const today = new Date();
    this.currentDate = today.toLocaleDateString('EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    });
  }

  /**
   * Handle form submission
   */
  onSubmit(): void {
    if (this.formGroup.valid) {
      console.log('Form Submitted Successfully:', this.formGroup.value);
      alert('تم الحفظ بنجاح!');
    } else {
      alert('يرجى التحقق من صحة النموذج قبل الإرسال.');
      this.formGroup.markAllAsTouched(); // Highlights invalid fields
    }
  }
}
<!DOCTYPE html>
<html lang="ar" dir="rtl">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>نموذج إدخال</title>
    <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.rtl.min.css">

</head>
<body>
    <!-- Top Bar -->
    <div class="top-bar d-flex align-items-center mb-3">
        <button class="btn btn-outline-light me-2">بطاقة وصفية لرسم عقاري</button>
        <button class="btn btn-outline-light me-2">العمليات الجارية</button>
        <div class="date-container mx-auto">
            <span id="date" class="date-display">{{ currentDate }}</span>
        </div>
        <button class="btn btn-outline-light me-2">
            <img src="assets/save1.png" alt="Icon 1">
        </button>
        <button class="btn btn-outline-light">
            <img src="assets/reset1.png" alt="Icon 2">
        </button>
    </div>

    <!-- Main Form Section -->
    <div class="container form-container">
        <div class="card shadow-sm">
            <div class="card-header bg-primary text-white text-center">
                <h3 class="mb-0">نموذج إدخال</h3>
            </div>
            <div class="card-body">
                <form [formGroup]="formGroup" (ngSubmit)="onSubmit()">
                    <!-- Form content goes here -->
                    <!-- Example of a form row -->
                    <div class="row">
                        <div class="col-md-6 mb-3">
                            <label for="name" class="form-label">الاسم</label>
                            <input type="text" class="form-control" id="name" formControlName="name" placeholder="ادخل الاسم">
                        </div>
                        <div class="col-md-6 mb-3">
                            <label for="surname" class="form-label">اللقب</label>
                            <input type="text" class="form-control" id="surname" formControlName="surname" placeholder="ادخل اللقب">
                        </div>
                    </div>
                    <div class="text-center">
                        <button type="submit" class="btn btn-primary">حفظ</button>
                        <button type="reset" class="btn btn-secondary">إعادة تعيين</button>
                    </div>
                </form>
            </div>
        </div>
    </div>
</body>
</html>
