import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-mainpage',
  templateUrl: './mainpagecomponent.html',
  styleUrls: ['./mainpagecomponent.css']
})
export class MainPageComponent implements OnInit {
  formGroup!: FormGroup; // Form group to handle form controls
  currentDate: string = ''; // Current date to display in the top bar

  constructor(private fb: FormBuilder) {}

  ngOnInit(): void {
    this.initializeForm();
    this.setCurrentDate();
  }

  // Initialize the form with validation
  private initializeForm(): void {
    this.formGroup = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      surname: ['', [Validators.required, Validators.minLength(3)]],
      identityDocument: ['', Validators.required],
      countNumber: ['', [Validators.required, Validators.pattern('^[0-9]+$')]],
      issueDate: ['', Validators.required],
      issuePlace: ['', Validators.required],
      entityName: [''],
      address: ['', Validators.required],
      documentName: ['', Validators.required]
    });
  }

  // Set the current date in the format YYYY-MM-DD
  private setCurrentDate(): void {
    const today = new Date();
    this.currentDate = today.toISOString().split('T')[0];
  }

  // Handle form submission
  onSubmit(): void {
    if (this.formGroup.valid) {
      console.log('Form Data:', this.formGroup.value);
      alert('تم الحفظ بنجاح!');
    } else {
      alert('يرجى التحقق من صحة النموذج قبل الإرسال.');
      this.formGroup.markAllAsTouched(); // Mark all fields as touched to display validation messages
    }
  }
}
