import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-deminscri',
  templateUrl: './deminscri.component.html',
  styleUrls: ['./deminscri.component.css'],
})
export class DeminscriComponent implements OnInit {
  item: string | null = null;
  title: string = 'Form';
  formGroup!: FormGroup;
  dynamicFields: any[] = [];

  // Configuration for forms based on the selected item
  formsConfig: { [key: string]: any } = {
    'mapping-request': {
      title: 'مطلب ترسيم',
      fields: [
        { label: 'Field 1', type: 'text', formControlName: 'field1' },
        { label: 'Field 2', type: 'number', formControlName: 'field2' },
      ],
    },
    'ownership-deed': {
      title: 'سند ملكية',
      fields: [
        { label: 'Name', type: 'text', formControlName: 'name' },
        { label: 'Document ID', type: 'text', formControlName: 'documentId' },
      ],
    },
    // Add more forms here...
  };

  constructor(private route: ActivatedRoute, private fb: FormBuilder) {}

  ngOnInit(): void {
    // Get the item from the route
    this.item = this.route.snapshot.paramMap.get('item');

    // Load the form configuration based on the item
    const formConfig = this.formsConfig[this.item || ''];

    if (formConfig) {
      this.title = formConfig.title;
      this.dynamicFields = formConfig.fields;

      // Initialize the form group dynamically
      const formControls: { [key: string]: any } = {};
      formConfig.fields.forEach((field: any) => {
        formControls[field.formControlName] = ['', Validators.required];
      });

      this.formGroup = this.fb.group(formControls);
    } else {
      console.error('No form configuration found for:', this.item);
    }
  }

  // Handle form submission
  onSubmit(): void {
    if (this.formGroup.valid) {
      console.log('Form Submitted for:', this.item);
      console.log('Form Values:', this.formGroup.value);
      alert('Form submitted successfully!');
    } else {
      alert('Please fill all required fields.');
      this.formGroup.markAllAsTouched();
    }
  }
}
<div class="container mt-5">
  <h2>{{ title }}</h2>

  <!-- Specific logic for the first item -->
  <ng-container *ngIf="item === 'mapping-request'">
    <p>This is the specific form for the first item: "mapping-request".</p>
  </ng-container>

  <!-- Dynamic Form -->
  <form [formGroup]="formGroup" (ngSubmit)="onSubmit()">
    <div *ngFor="let field of dynamicFields" class="mb-3">
      <label [for]="field.formControlName" class="form-label">{{ field.label }}</label>

      <!-- Render input field dynamically -->
      <input
        [type]="field.type"
        [id]="field.formControlName"
        [formControlName]="field.formControlName"
        class="form-control"
      />

      <!-- Validation message -->
      <div
        *ngIf="
          formGroup.get(field.formControlName)?.invalid &&
          formGroup.get(field.formControlName)?.touched
        "
        class="text-danger"
      >
        {{ field.label }} is required.
      </div>
    </div>

    <!-- Submit button -->
    <button type="submit" class="btn btn-primary" [disabled]="formGroup.invalid">
      Submit
    </button>
  </form>
</div>
