        form input:focus,
        form select:focus {
            border-color: #007bff;
            box-shadow: 0 0 5px rgba(0, 123, 255, 0.5);
            outline: none;
        }

        .card {
            max-height: 100%;
        }
    </style>
</head>
<body>
    <!-- Top Bar -->
    <div class="top-bar d-flex align-items-center mb-3">
        <button class="btn btn-outline-light me-2">بطاقة وصفية لرسم عقاري</button>
        <button class="btn btn-outline-light me-2">العمليات الجارية</button>
        <div class="date-container mx-auto">
            <span id="date" class="date-display">{{ currentDate }}</span>
        </div>
        <button class="btn btn-outline-light me-2">
            <img src="assets/save1.png" alt="Icon 1">
        </button>
        <button class="btn btn-outline-light">
            <img src="assets/reset1.png" alt="Icon 2">
        </button>
    </div>

    <!-- Main Form Section -->
    <div class="container">
        <div class="card shadow-sm">
            <div class="card-header bg-primary text-white text-center">
                <h3 class="mb-0">نموذج إدخال</h3>
            </div>
            <div class="card-body">
                <form [formGroup]="formGroup" (ngSubmit)="onSubmit()">
                    <!-- Row 1: Name and Surname -->
                    <div class="row">
                        <div class="col-md-6 mb-3">
                            <label for="name" class="form-label">الاسم</label>
                            <input type="text" class="form-control" id="name" formControlName="name" placeholder="ادخل الاسم">
                        </div>
                        <div class="col-md-6 mb-3">
                            <label for="surname" class="form-label">اللقب</label>
                            <input type="text" class="form-control" id="surname" formControlName="surname" placeholder="ادخل اللقب">
                        </div>
                    </div>

                    <!-- Row 2: Identity Document -->
                    <div class="row">
                        <div class="col-md-6 mb-3">
                            <label for="identityDocument" class="form-label">وثيقة الهوية</label>
                            <select class="form-select" id="identityDocument" formControlName="identityDocument">
                                <option selected disabled>اختر وثيقة الهوية</option>
                                <option value="جواز السفر">جواز السفر</option>
                                <option value="بطاقة الهوية">بطاقة الهوية</option>
                                <option value="رخصة القيادة">رخصة القيادة</option>
                            </select>
                        </div>
                    </div>

                    <!-- Row 3: Count Number, Issue Date, and Issue Place -->
                    <div class="row">
                        <div class="col-md-4 mb-3">
                            <label for="countNumber" class="form-label">عددها</label>
                            <input type="text" class="form-control" id="countNumber" formControlName="countNumber" placeholder="ادخل عددها">
                        </div>
                        <div class="col-md-4 mb-3">
                            <label for="issueDate" class="form-label">تاريخ اصدارها</label>
                            <input type="date" class="form-control" id="issueDate" formControlName="issueDate">
                        </div>
                        <div class="col-md-4 mb-3">
                            <label for="issuePlace" class="form-label">مكان اصدارها</label>
                            <input type="text" class="form-control" id="issuePlace" formControlName="issuePlace" placeholder="ادخل مكان الاصدار">
                        </div>
                    </div>

                    <!-- Row 4: Entity Name -->
                    <div class="row">
                        <div class="col-md-12 mb-3">
                            <label for="entityName" class="form-label">تسمية الذات الإعتبارية</label>
                            <input type="text" class="form-control" id="entityName" formControlName="entityName" placeholder="ادخل تسمية الذات الإعتبارية">
                        </div>
                    </div>

                    <!-- Row 5: Address -->
                    <div class="row">
                        <div class="col-md-12 mb-3">
                            <label for="address" class="form-label">العنوان</label>
                            <input list="addressOptions" class="form-control" id="address" formControlName="address" placeholder="اختر العنوان">
                            <datalist id="addressOptions">
                                <option value="المدينة"></option>
                                <option value="القرية"></option>
                                <option value="المنطقة"></option>
                                <option value="المكتب الرئيسي"></option>
                                <option value="العنوان الفرعي"></option>
                            </datalist>
                        </div>
                    </div>

                    <!-- Row 6: Payment Method -->
                    <div class="row">
                        <div class="col-md-12 mb-3">
                            <label for="paymentMethod" class="form-label">كيفية الدفع</label>
                            <select class="form-select" id="paymentMethod" formControlName="paymentMethod">
                                <option selected disabled>اختر كيفية الدفع</option>
                                <option value="صك بنكي">صك بنكي</option>
                                <option value="حوالة بريدية">حوالة بريدية</option>
                                <option value="دفع إلكتروني">دفع إلكتروني</option>
                                <option value="نقداً">نقداً</option>
                            </select>
                        </div>
                    </div>

                    <!-- Submit Buttons -->
                    <div class="text-center">
                        <button type="submit" class="btn btn-primary">حفظ</button>
                        <button type="reset" class="btn btn-secondary">إعادة تعيين</button>
                    </div>
                </form>
            </div>
        </div>
    </div>
</body>
</html>
